"},{"components":[{"internalType":"address","name":"provider","type":"address"},{"internalType":"uint256","name":"bid","type":"uint256"},{"internalType":"uint256","name":"providerUpVotes","type":"uint256"},{"internalType":"uint256","name":"providerDownVotes","type":"uint256"}],"internalType":"struct AuctionsManager.ProviderBid[]","name":"providerBids","type":"tuple[]"},{"components":[{"internalType":"address","name":"provider","type":"address"},{"internalType":"uint256","name":"bid","type":"uint256"}],"internalType":"struct AuctionsManager.WinnerBid","name":"winnerBid","type":"tuple"},{"internalType":"enum AuctionsManager.AuctionState","name":"auctionState","type":"uint8"},{"internalType":"uint256","name":"lastUpdateTimestamp","type":"uint256"}],"internalType":"struct AuctionsManager.Auction[]","name":"","type":"tuple[]"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"getOwner","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"getTasksManager","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"bytes32","name":"_auctionID","type":"bytes32"}],"name":"getWinnerBid","outputs":[{"components":[{"internalType":"address","name":"provider","type":"address"},{"internalType":"uint256","name":"bid","type":"uint256"}],"internalType":"struct AuctionsManager.WinnerBid","name":"","type":"tuple"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address payable","name":"_tasksManager","type":"address"}],"name":"setTasksManager","outputs":[],"stateMutability":"nonpayable","type":"function"}],"devdoc":{"kind":"dev","methods":{},"version":1},"userdoc":{"kind":"user","methods":{},"version":1}},"settings":{"compilationTarget":{"contracts/AuctionsManager.sol":"AuctionsManager"},"evmVersion":"london","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimize